import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsloading] = useState(false);
  const [erro, setError] = useState(null);

  async function fetchMoviesHandler() {
    setIsloading(true);

    try {
      const response = await fetch("https://swapi.dev/api/films/");
      if (!response.ok) {
        return new Error("something went wrong");
      }
      const data = await response.json();

      const transformedMovies = data.results.map((dataMovies) => {
        return {
          id: dataMovies.episode_id,
          title: dataMovies.title,
          releaseDate: dataMovies.release_date,
          openingText: dataMovies.opening_crawl,
        };
      });
      setMovies(transformedMovies);
      setIsloading(false);
    } catch (erro) {}
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && <h3> Found no Movies!</h3>}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
